import React from 'react'

const formProvider = {
    name:"",
    email:"",
    password:"",
    nameError:"",
    emailError:"",
    passError:""
}
export class SignUp extends React.Component{
    state = formProvider


    handleChange = event => {
        const isCheckBox = event.target.type === "checkbox"
        this.setState({
        [event.target.name] : isCheckBox ? event.target.checked : event.target.value
        })
    }

    validate = () => {
        let nameError = ''
        let emailError = ""
        let passError = ""


        if(!this.state.name){
            nameError = "It can't be left it blank!"
        }
        if(this.state.email.includes("@")){
            emailError = "Invalid address"
        }
        if(this.state.password.length <= 8){
            passError = "Password must contain at least 8 elements"
        }
        if(nameError || emailError || passError){
            this.setState({nameError, emailError, passError})
        }

    }

// Handle Submit function that makes form elements active

    handleSubmit = event => {
        event.preventDefault();
        this.validate()
        this.setState(formProvider)
    }

    render() {
        return(
            <>
                <div className='text-container'>
                    <h1 className='header-text'>Follow your interest</h1>
                    <p>Instant updates from our company experts, favorite celebrities, and what's happening around the world.</p>
                    <input type="search"/>
                </div>
                 <form className='form-elements'>
                         <h3>New to SAFETY BANK? Join today!</h3>
                        <input type="text"
                               name="name"
                               autoComplete='off'
                               placeholder="Full name"
                               value={this.state.name}
                               onChange={this.handleChange}
                        />
                     <div>{this.state.nameError}</div>
                         <input type="email" name="email" autoComplete='off' placeholder="Email" value={this.state.email}
                                onChange={this.handleChange}/>
                     <div>{this.state.emailError}</div>
                        <input type='password' name='password' placeholder="Password"  onChange={this.handleChange}/>
                     <div>{this.state.passError}</div>
                        <button type='button'>Sign Up</button>
                 </form>
            </>
        )
    }
}
