import React from 'react'

const initialState = {
    name:"",
    email:"",
    password:"",
    nameError:"",
    emailError:"",
    passError:""
}
export default class SignUp extends React.Component{
    state = initialState;


    handleChange = event => {
        const isCheckBox = event.target.type === "checkbox"
        this.setState({
        [event.target.name] : isCheckBox ? event.target.checked : event.target.value
        })
    }

    validate = () => {
       let emailError = ""
       let nameError = ""
       let passError = ""
        if(!this.state.email.includes("@")){
            emailError = "Invalid address"
        }
        if(!this.state.name) {
            nameError = "It "
        }
        if(emailError){
            this.setState({emailError})
            return false
        }

return true
    }

// Handle Submit function that makes form elements active

    handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate()
        if(isValid){
            console.log (isValid)
            this.setState(initialState)
        }


    }
    render() {
        return(
            <>
                <div className='text-container'>
                    <h1 className='header-text'>Follow your interest</h1>
                    <p>Instant updates from our company experts, favorite celebrities, and what's happening around the world.</p>
                    <input type="search"/>
                </div>
                 <form className='form-elements' onSubmit={this.handleSubmit}>
                         <h3>New to SAFETY BANK? Join today!</h3>
                        <input
                               name="name"
                               autoComplete='off'
                               placeholder="Full name"
                               value={this.state.name}
                               onChange={this.handleChange}/>
                               <div>{this.state.nameError}</div>
                         <input
                                name="email"
                                autoComplete='off'
                                placeholder="Email"
                                value={this.state.email}
                                onChange={this.handleChange}/>
                             <div>{this.state.emailError}</div>
                        <input
                               name='password'
                               placeholder="Password"
                               value={this.state.password}
                               onChange={this.handleChange}/>
                            <div>{this.state.passError}</div>
                        <button type='submit'>Sign Up</button>
                 </form>
            </>
        )
    }
}
